const HockeyField = () => {
  const [player1, setPlayer1] = useState({ x: 100, y: 300, r: 10 });
  const [player2, setPlayer2] = useState({ x: 700, y: 300, r: 10 });
  const [ball, setBall] = useState({ x: 400, y: 300, r: 10 });

  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:8765');

    ws.current.onopen = () => {
      console.log('WebSocket connection opened');
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log(data);
      setPlayer2({ x: data.player2.position[0], y: data.player2.position[1], r: data.player2.radius });
      setBall({ x: data.ball.position[0], y: data.ball.position[1], r: data.ball.radius });
    };

    ws.current.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      ws.current.close();
    };
  });

  const handleMouseMove = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    setPlayer1({ x, y });

    // Send player1 position to backend
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ dx: x - player1.x, dy: y - player1.y }));
    }
  };

  const handleKeyDown = useCallback((event) => {
    let dx = 0;
    let dy = 0;
    if (event.key === 'ArrowLeft') {
      dx = -10;
    } else if (event.key === 'ArrowRight') {
      dx = 10;
    } else if (event.key === 'ArrowUp') {
      dy = -10;
    } else if (event.key === 'ArrowDown') {
      dy = 10;
    }

    if (dx !== 0 || dy !== 0) {
      setPlayer1(prev => ({ x: prev.x + dx, y: prev.y + dy }));

      // Send player1 position to backend
      if (ws.current && ws.current.readyState === WebSocket.OPEN) {
        ws.current.send(JSON.stringify({ dx, dy }));
      }
    }
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);

  return (
    <div className="hockey-field" onMouseMove={handleMouseMove}>
      <Player x={player1.x} y={player1.y} />
      <Player x={player2.x} y={player2.y} isOpponent />
      <Ball x={ball.x} y={ball.y} />
      <div className="position-info">
        <p>Ball Position: ({ball.x}, {ball.y})</p>
      </div>
    </div>
  );
};

export default HockeyField;
