import React, { useEffect, useState, useRef } from 'react';
import Player from './Player';
import Ball from './Ball';
import '../cyberpunk-theme.css';

const HockeyField = () => {
  const [player1, setPlayer1] = useState({ x: 100, y: 300 });
  const [player2, setPlayer2] = useState({ x: 700, y: 300 });
  const [ball, setBall] = useState({ x: 400, y: 300 });

  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:8765');

    ws.current.onopen = () => {
      console.log('WebSocket connection opened');
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.player2 && data.player2.position) {
        setPlayer2({ x: data.player2.position[0], y: data.player2.position[1] });
      }
      if (data.ball && data.ball.position) {
        setBall({ x: data.ball.position[0], y: data.ball.position[1] });
      }
    };

    ws.current.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      ws.current.close();
    };
  }, []);

  const handleMouseMove = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    setPlayer1({ x, y });

    // Send player1 position to backend
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ dx: x - player1.x, dy: y - player1.y }));
    }
  };

  return (
    <div className="hockey-field" onMouseMove={handleMouseMove}>
      <Player x={player1.x} y={player1.y} />
      <Player x={player2.x} y={player2.y} isOpponent />
      <Ball x={ball.x} y={ball.y} />
      <div className="position-info">
        <p>Ball Position: ({ball.x}, {ball.y})</p>
      </div>
    </div>
  );
};

export default HockeyField;
